const result = [];

for (const item of $input.all()) {
  const rawExp = String(item.json["Experience"] || "");
  const match = rawExp.match(/\d+/);
  let expYears = match ? parseInt(match[0]) : 0;

  // Map experience to level 1–5
  let experienceLevel = "1";
  if (expYears >= 1 && expYears <= 2) experienceLevel = "2";
  else if (expYears >= 3 && expYears <= 4) experienceLevel = "3";
  else if (expYears >= 5 && expYears <= 7) experienceLevel = "4";
  else if (expYears >= 8) experienceLevel = "5";

  // Clean resume link (convert Google Drive share link to direct download)
  const rawResume = item.json["Resume"] || "";
  const matchId = rawResume.match(/\/d\/([^/]+)\//);
  const fileId = matchId && matchId[1] ? matchId[1] : "";

  const fixedResumeLink = fileId
    ? `https://drive.google.com/uc?export=download&id=${fileId}`
    : rawResume; // fallback if not Google Drive

  // Final item
  result.push({
    json: {
      name: (item.json["Consultant Name"] || "").toLowerCase().replace(/\s+/g, ''),
      techStack: item.json["Tech Stack"] || "",
      experienceLevel,
      location: item.json["Location"] || "",
      relocation: item.json["Relocation"] || "",
      resumeLink: fixedResumeLink
    }
  });
}

return result;

// Get input data depending on mode
const item = $input.item || {};
const json = item.json || $json || {};

// Check if 'columns' exists and is a string
if (!json.columns || typeof json.columns !== 'string') {
  throw new Error("Missing or invalid 'columns' field in input.");
}

// Split the comma-separated string
const headersArray = json.columns.split(',');

// Build the headers object
const headersObject = {};
headersArray.forEach(header => {
  const trimmedHeader = header.trim();
  headersObject[trimmedHeader] = trimmedHeader;
});

// Return result
return [
  {
    json: headersObject
  }
];

const input1 = $input.all()[0]?.json || {};
const input2 = $input.all()[1]?.json || {};

return [
  {
    json: {
      description: input1.description || input2.description || '',
      text: input1.text || input2.text || '',
    }
  }
];

const content = $json.choices?.[0]?.message?.content || {};

return [
  {
    json: {
      job_skills: content.job_skills || {},
      resume_skills: content.resume_skills || {},
      missing_skills: content.missing_skills || {}
    }
  }
];
